mapper.update:
UPDATE setmeal
category_id = #{categoryId}, description = #{description}, image = #{image}, name = #{name}, price = #{price}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
WHERE id = #{id}

mapper.select:
SELECT s.*, c.name categoryName FROM setmeal s LEFT JOIN category c ON s.category_id = c.id
AND s.name LIKE '%${name}%'
AND s.status = #{status}
AND s.category_id = #{categoryId}
ORDER BY update_time DESC
